import React, { Component } from "react";

class TimerForm extends Component {
  state = {
    title: this.props.title || "",
    projet: this.props.projet || "",
  };
  render() {
    const submitText = this.props.title ? "Modifier" : "crée";
    return (
      <div className="form">
        <div className="form--content">
          <div className="form--item">
            <label>Titre</label>
            <input
              type="text"
              placeholder="mon Titre"
              value={this.state.title}
              onChange={this.handleChangeTitle}
            />
          </div>
          <div className="form--item">
            <label>Projet</label>
            <input type="text" placeholder="mon Projet" />
          </div>
        </div>
        <div className="form--button">
          <button className="button btn--submit">{submitText}</button>
          <button className="button btn--cancel">Annuler</button>
        </div>
      </div>
    );
  }
}
export default TimerForm;
