import React, { Component } from "react";
import "../helpers.js";

class Timer extends Component {
  renderButton() {
    if (this.props.runningSince) {
      return <button className="button">Play</button>;
    } else {
      <button className="button">Pause</button>;
    }
  }
  render() {
    const elapsedString = window.helpers.renderElapsedString(
      this.props.elapsed,
      this.props.runningSince
    );
    return (
      <div className="timer--box">
        <div className="timer--content">
          <div className="timer--header">
            <h2>{this.props.title}</h2>
          </div>
          <div className="timer--meta">
            <p>{this.props.project}</p>
          </div>
          <div className="timer--h2">
            <h4>{elapsedString}</h4>
          </div>
          <div className="actions">
            <span
              onClick={() => {
                this.props.onDelete(this.props.id);
              }}
              className="trash"
            >
              Delete
            </span>
            <span onClick={this.props.onEditFormOpen} className="edit">
              Update
            </span>
          </div>
          <button className="button">Play</button>
        </div>
      </div>
    );
  }
}
export default Timer;
